sqlMap.insert:
/* RESERVE.reserveSaveInfo */
INSERT INTO RESERVATION_INFO( RESERVATION_PWD , NAME , FIRM_NAME , PHONE_NUMBER_1 , PHONE_NUMBER_2 , PHONE_NUMBER_3 , EMAIL_ADDR_1 , EMAIL_ADDR_2 , EMAIL_ADDR_3 , REQUEST , AGREE_TERM_PERSONAL_INFO , TOTAL_PRICE , STATUS , STATUS_TIME ) VALUES ( #reservation_pwd# , #name# , #firm_name# , #phone_number_1# , #phone_number_2# , #phone_number_3# , #email_addr_1# , #email_addr_2# , #email_addr_3# , #request# , #agree_term_personal_info# , #total_price# , 'W' , NOW() )
SELECT @@IDENTITY AS RESERVATION_KEY

sqlMap.select:
/* RESERVE.getReservationList */
SELECT RI.RESERVATION_KEY , RI.NAME , RI.FIRM_NAME , RI.PHONE_NUMBER_1 , RI.PHONE_NUMBER_2 , RI.PHONE_NUMBER_3 , RI.EMAIL_ADDR_1 , RI.REQUEST , RI.STATUS , CASE STATUS WHEN 'Y' THEN '승인' WHEN 'W' THEN '신청' WHEN 'R' THEN '반려' END STATUS_NAME , RI.STATUS_TIME , IFNULL(RI.EMAIL_ADDR_2, RI.EMAIL_ADDR_3) EMAIL_ADDR_2 , IFNULL(RI.EMAIL_ADDR_3, RI.EMAIL_ADDR_2) EMAIL_ADDR_3 , RI.TOTAL_PRICE , (SELECT NAME FROM MEETINGROOM_INFO MI INNER JOIN RESERVATION_MAPP AS RM ON RM.MEETINGROOM_KEY = MI.MEETINGROOM_KEY WHERE RM.RESERVATION_KEY = RI.RESERVATION_KEY LIMIT 1) MEETINGROOM_NAME , (SELECT RESERVATION_DATE FROM RESERVATION_MAPP AS RM WHERE RM.RESERVATION_KEY = RI.RESERVATION_KEY LIMIT 1) RESERVATION_DATE , (SELECT DATE_FORMAT(REG_TIME,'%Y-%m-%d') FROM RESERVATION_MAPP AS RM WHERE RM.RESERVATION_KEY = RI.RESERVATION_KEY LIMIT 1) REG_TIME FROM RESERVATION_INFO AS RI WHERE 1 = 1
ORDER BY RESERVATION_KEY LIMIT #recordRowNum# , #pageSize#
